<p>Formulario de edición</p>
<div class="modal fade" id="editTransporterModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Editar Transportista</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form (ngSubmit)="onSubmit()" #editTransporterForm="ngForm">
          <div class="form-group">
            <label for="name">Nombre:</label>
            <input type="text" id="name" name="name" [(ngModel)]="toUpdateTransporter.name" class="form-control" required>
          </div>
          <div class="form-group">
            <label for="company">Empresa:</label>
            <input type="text" id="company" name="company" [(ngModel)]="toUpdateTransporter.company" class="form-control" required>
          </div>
          <!-- Otros campos del Transporter, si los hay -->
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Cerrar</button>
        <button type="submit" class="btn btn-primary">Guardar Cambios</button>
      </div>
    </div>
  </div>
</div>
